import { useState } from 'react';
import Input from '../../components/Input';
import './index.scss';
import * as Axios from 'axios';

const Tambah = () => {
  const [name, setName]= useState("");
  const [price, setPrice]= useState(0);
  const [stock, setStock]= useState(0);
  const [status, setStatus]= useState(true);
  const addProduct = () => {
    Axios.post("http://localhost:3000/product",{
      name: name,
      price: price,
      stock: stock,
      status: status,
    });
  };
  return (
    <div className="main">
      <div className="card">
        <h2>Tambah Produk</h2>
        <br />
        <form>
          <Input type="text"
          onChange={(event)=>{
            setName(event.target.value);
          }} placeholder="Nama Produk..." label="Nama"/>
          <Input type="number" name="price"
          onChange={(event)=>{
            setPrice(event.target.value);
          }}
          placeholder="Harga Produk..." label="Harga"/>
          <Input type="number" name="stock" 
          onChange={(event)=>{
            setStock(event.target.value);
          }}
          placeholder="Stock Produk..." label="Stock"/>
          <Input type="checkbox" label="Active" name="status" 
          onChange={(event)=>{
            setStatus(event.target.value);
          }}
          />
          <button onClick={addProduct} className="btn btn-primary">Simpan</button>
        </form>
      </div>
    </div>
  )
}

export default Tambah;
