import { useParams } from "react-router-dom";
import Input from "../../components/Input";
import { useState, useEffect } from "react";
import * as Axios from "axios";

const Edit = () => {
  const { id } = useParams();
  const [produtItem, setProductItem] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState(0);
  const [status, setStatus] = useState(false);
  // const history = useHistory();
  useEffect(() => {
    Axios.get(`http://localhost:3000/product/${id}`).then((response) => {
      setProductItem(response.data);
    });
  }, []);
  const updateProduct = (id) => {
    Axios.put(`http://localhost:3000/product/${id}`, {
      name: 'name',
      price: 'price',
      stock: stock,
      status: status,
    }).then((response) => {
      console.log(response)
    })
  }

  // console.log(id);
  return (
    <div className="main">
      <div className="card">
        <h2>Edit Produk</h2>
        <br />
        {produtItem.map((item, i) =>
          <div key={i}>
            <Input type="text" placeholder="Nama Produk..." label="Nama"
              defaultValue={item.name}
              onChange={(event) => {
                setName({ ...item.name, name: event.target.value });
              }}/>
            <Input type="number" placeholder="Harga Produk..." label="Harga"
                defaultValue={item.price}
                onChange={(event) => {
                  setPrice({ ...item.price, price: event.target.value });
              }} />
              <Input type="number" placeholder="Stock Produk..." label="Stock"
              defaultValue={item.stock}
              onChange={(event) => {
                setStock({ ...item.stock, stock: event.target.value });
              }} />
            <Input type="checkbox" label="Active"
              defaultValue={item.status}
              onChange={(event) => {
                setStatus({ ...item.status, status: event.target.value });
              }}
            />
            <button onClick={() =>updateProduct(item._id)} className="btn btn-primary">Simpan</button>
          </div>
          )}
        </div>
    </div>
  )
}

export default Edit;
