    //   price: price.price,
    //   stock: stock.stock,
    //   status: status.status,
    // }).then((response) => {
    //   console.log(response)
    // })
  }
  return (
    <div className="main">
      <div className="card">
        <h2>Edit Produk</h2>
        <br />
        {detail.map((item,i)=>
            <div key={id}>
            <Input name="nama" type="text" label="Nama" 
            value={item.name}
              onChange={(event) => { setName({ ...item, nama: event.target.value }) }}
            />
            <Input type="number" placeholder="Harga Produk..." label="Harga"
                value={item.price}
                onChange={(event) => {
                  // setHarga({ ...item, harga: event.target.value });
              }} />
              <Input type="number" placeholder="Stock Produk..." label="Stock"
              value={item.stock}
              onChange={(event) => {
                // setJumlah({ ...item, jumlah: event.target.value });
              }} />
            <Input type="checkbox" label="Active"
              value={item.status}
              onChange={(event) => {
                // setKondisi({ ...item, kondisi: event.target.value });
              }}
            />
              <button onClick={() => updateProduct(item._id)} className="btn btn-primary">Simpan</button>
        </div>
        )}
      </div>
    </div>
  )
}

export default Edit;
