import { useParams } from "react-router-dom";
import Input from "../../components/Input";
import { useState, useEffect } from "react";
import * as Axios from "axios";

const Edit = () => {
  const { id } = useParams();
  const [detail, setDetail] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState(0);
  const [status, setStatus] = useState(false);
  // const history = useHistory();
  useEffect(() => {
    Axios.get('http://localhost:3000/product/' + id).then((response) => {
      setDetail(response.data);
    });
  }, []);
  const updateProduct = (id) => {
    Axios.put(`http://localhost:3000/product/{$id}`,{
      name,price,stock,status
    }).then((response) => {
      console.log(response)
    })
  }

  // console.log(id);
  return (
    <div className="main">
      <div className="card">
        <h2>Edit Produk</h2>
        <br />
        {detail.map((item, i) =>
          <div key={i}>
            <Input type="text" placeholder="Nama Produk..." label="Nama"
              defaultValue={item.name}
              onChange={(event) => {
                setName({ ...item, name: event.target.value });
              }}/>
            <Input type="number" placeholder="Harga Produk..." label="Harga"
                defaultValue={item.price}
                onChange={(event) => {
                  setPrice({ ...item, price: event.target.value });
              }} />
              <Input type="number" placeholder="Stock Produk..." label="Stock"
              defaultValue={item.stock}
              onChange={(event) => {
                setStock({ ...item, stock: event.target.value });
              }} />
            <Input type="checkbox" label="Active"
              onChange={(event) => {
                setStatus({ ...item, status: event.target.checked });
              }}
            />
            <button onClick={() => updateProduct()} className="btn btn-primary">Simpan</button>
          </div>
          )}
        </div>
    </div>
  )
}

export default Edit;
